import React from 'react';
import { auth } from '../firebaseConfig';
import { useNavigate } from 'react-router-dom';

const UserAccount = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    auth.signOut().then(() => {
      navigate('/login');
    });
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <h1>My Account</h1>
      <p>Welcome to your account page. Here you can manage your profile and orders.</p>
      <button onClick={handleLogout} style={{ backgroundColor: '#d32f2f', color: 'white', border: 'none', padding: '10px 20px', cursor: 'pointer' }}>
        Logout
      </button>
    </div>
  );
};

export default UserAccount;
