import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/SwiggyStyle.css';

const OrderPage = () => {
  const navigate = useNavigate();

  const [address, setAddress] = useState({
    fullName: '',
    street: '',
    city: '',
    state: '',
    zip: '',
    phone: '',
  });

  const [paymentMethod, setPaymentMethod] = useState('cod'); // cod = Cash on Delivery

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAddress((prev) => ({ ...prev, [name]: value }));
  };

  const handlePaymentChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you can add order submission logic, e.g., save order to Firebase
    alert('Order placed successfully!');
    navigate('/'); // Redirect to home or order confirmation page
  };

  return (
    <div className="order-page-container">
      <h1>Place Your Order</h1>
      <form onSubmit={handleSubmit} className="order-form">
        <h2>Delivery Address</h2>
        <label>
          Full Name
          <input type="text" name="fullName" value={address.fullName} onChange={handleChange} required />
        </label>
        <label>
          Street Address
          <input type="text" name="street" value={address.street} onChange={handleChange} required />
        </label>
        <label>
          City
          <input type="text" name="city" value={address.city} onChange={handleChange} required />
        </label>
        <label>
          State
          <input type="text" name="state" value={address.state} onChange={handleChange} required />
        </label>
        <label>
          ZIP Code
          <input type="text" name="zip" value={address.zip} onChange={handleChange} required />
        </label>
        <label>
          Phone Number
          <input type="text" name="phone" value={address.phone} onChange={handleChange} required />
        </label>

        <h2>Payment Method</h2>
        <label>
          <input
            type="radio"
            name="paymentMethod"
            value="cod"
            checked={paymentMethod === 'cod'}
            onChange={handlePaymentChange}
          />
          Cash on Delivery
        </label>
        <label>
          <input
            type="radio"
            name="paymentMethod"
            value="card"
            checked={paymentMethod === 'card'}
            onChange={handlePaymentChange}
          />
          Credit/Debit Card (Coming Soon)
        </label>

        <button type="submit" className="btn-place-order">
          Place Order
        </button>
      </form>
    </div>
  );
};

export default OrderPage;
