import React, { useState } from 'react';
import { auth } from '../firebaseConfig';
import { useNavigate } from 'react-router-dom';
import FoodCategories from './FoodCategories';
import RestaurantList from './RestaurantList';
import CartSidebar from './CartSidebar';

const Home = () => {
  const navigate = useNavigate();
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [cartItems, setCartItems] = useState([]);

  const handleLogout = () => {
    auth.signOut().then(() => {
      navigate('/login');
    });
  };

  const handleSelectCategory = (category) => {
    setSelectedCategory(category);
  };

  const handleAddToCart = (item) => {
    setCartItems(prevItems => {
      const existingItem = prevItems.find(i => i.id === item.id);
      if (existingItem) {
        return prevItems.map(i =>
          i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
        );
      } else {
        return [...prevItems, { ...item, quantity: 1 }];
      }
    });
  };

  const handleRemoveFromCart = (itemId) => {
    setCartItems(prevItems => prevItems.filter(i => i.id !== itemId));
  };

  const handleUpdateQuantity = (itemId, quantity) => {
    if (quantity < 1) return;
    setCartItems(prevItems =>
      prevItems.map(i =>
        i.id === itemId ? { ...i, quantity } : i
      )
    );
  };

  return (
    <>
      <header>
        <a href="/home" className="logo"><i className="fas fa-utensils"></i>food</a>
        <div id="menu-bar" className="fas fa-bars"></div>
        <nav className="navbar">
          <a href="/home">home</a>
          <a href="/food-item#speciality">our's special</a>
          <a href="/food-item#popular">popular foods</a>
          <a href="/home#gallery">gallery</a>
          <a href="/home#review">customer reviews</a>
          <a href="/order"><button className="btn0">Order Now</button></a>
          <button onClick={handleLogout} className="btn-logout">Logout</button>
        </nav>
      </header>

      <main className="main-content">
        <FoodCategories onSelectCategory={handleSelectCategory} />
        <RestaurantList selectedCategory={selectedCategory} onAddToCart={handleAddToCart} />
        <CartSidebar
          cartItems={cartItems}
          onRemoveFromCart={handleRemoveFromCart}
          onUpdateQuantity={handleUpdateQuantity}
        />
      </main>
    </>
  );
};

export default Home;
