import React from 'react';
import { useParams } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const foodData = {
  1: {
    id: 1,
    name: 'Cold Drink',
    variants: [
      { id: 'v1', name: 'Coke', price: 2, image: '/images/cold-drink-coke.jpg' },
      { id: 'v2', name: 'Pepsi', price: 2, image: '/images/cold-drink-pepsi.jpg' },
      { id: 'v3', name: 'Sprite', price: 2, image: '/images/cold-drink-sprite.jpg' },
      { id: 'v4', name: 'Fanta', price: 2, image: '/images/cold-drink-fanta.jpg' },
      { id: 'v5', name: 'Mountain Dew', price: 3, image: '/images/cold-drink-mountaindew.jpg' },
    ],
  },
  2: {
    id: 2,
    name: 'Ice Cream',
    variants: [
      { id: 'v1', name: 'Vanilla', price: 3, image: '/images/ice-cream-vanilla.jpg' },
      { id: 'v2', name: 'Chocolate', price: 3, image: '/images/ice-cream-chocolate.jpg' },
      { id: 'v3', name: 'Strawberry', price: 3, image: '/images/ice-cream-strawberry.jpg' },
      { id: 'v4', name: 'Mint', price: 3, image: '/images/ice-cream-mint.jpg' },
      { id: 'v5', name: 'Mango', price: 4, image: '/images/ice-cream-mango.jpg' },
    ],
  },
  3: {
    id: 3,
    name: 'Burger',
    variants: [
      { id: 'v1', name: 'Classic', price: 5, image: '/images/burger-classic.jpg' },
      { id: 'v2', name: 'Cheese', price: 6, image: '/images/burger-cheese.jpg' },
      { id: 'v3', name: 'Bacon', price: 7, image: '/images/burger-bacon.jpg' },
      { id: 'v4', name: 'Double Patty', price: 8, image: '/images/burger-double.jpg' },
      { id: 'v5', name: 'Veggie', price: 5, image: '/images/burger-veggie.jpg' },
    ],
  },
  4: {
    id: 4,
    name: 'Pizza',
    variants: [
      { id: 'v1', name: 'Margherita', price: 8, image: '/images/pizza-margherita.jpg' },
      { id: 'v2', name: 'Pepperoni', price: 10, image: '/images/pizza-pepperoni.jpg' },
      { id: 'v3', name: 'BBQ Chicken', price: 12, image: '/images/pizza-bbqchicken.jpg' },
      { id: 'v4', name: 'Veggie', price: 9, image: '/images/pizza-veggie.jpg' },
      { id: 'v5', name: 'Hawaiian', price: 11, image: '/images/pizza-hawaiian.jpg' },
    ],
  },
  5: {
    id: 5,
    name: 'Sweet',
    variants: [
      { id: 'v1', name: 'Gulab Jamun', price: 4, image: '/images/sweet-gulabjamun.jpg' },
      { id: 'v2', name: 'Rasgulla', price: 4, image: '/images/sweet-rasgulla.jpg' },
      { id: 'v3', name: 'Jalebi', price: 3, image: '/images/sweet-jalebi.jpg' },
      { id: 'v4', name: 'Kheer', price: 5, image: '/images/sweet-kheer.jpg' },
      { id: 'v5', name: 'Barfi', price: 4, image: '/images/sweet-barfi.jpg' },
    ],
  },
};

const FoodDetail = () => {
  const { id } = useParams();
  const foodItem = foodData[id];
  const { addToCart } = useCart();

  if (!foodItem) {
    return <div>Food item not found.</div>;
  }

  const handleAddToCart = (variant) => {
    addToCart({
      id: foodItem.id + '-' + variant.id,
      name: foodItem.name + ' - ' + variant.name,
      price: variant.price,
      image: variant.image,
      quantity: 1,
    });
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <h1>{foodItem.name}</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {foodItem.variants.map((variant) => (
          <div key={variant.id} style={{ width: 'calc(50% - 10px)', border: '1px solid #ddd', borderRadius: '8px', padding: '10px' }}>
            <img
              src={variant.image}
              alt={`${foodItem.name} - ${variant.name}`}
              style={{ width: '100%', borderRadius: '8px' }}
            />
            <h3>{variant.name}</h3>
            <p>${variant.price.toFixed(2)}</p>
            <button
              onClick={() => handleAddToCart(variant)}
              style={{
                backgroundColor: '#d32f2f',
                color: 'white',
                padding: '10px 20px',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                width: '100%',
              }}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FoodDetail;
