import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const foodData = {
  1: {
    id: 1,
    name: 'Margherita Pizza',
    variants: [
      { id: 'v1', name: 'Small', price: 5, image: '/images/p-1-small.jpg' },
      { id: 'v2', name: 'Medium', price: 8, image: '/images/p-1-medium.jpg' },
      { id: 'v3', name: 'Large', price: 12, image: '/images/p-1-large.jpg' },
      { id: 'v4', name: 'Extra Large', price: 15, image: '/images/p-1-xl.jpg' },
      { id: 'v5', name: 'Family Size', price: 20, image: '/images/p-1-family.jpg' },
    ],
  },
  2: {
    id: 2,
    name: 'Pepperoni Pizza',
    variants: [
      { id: 'v1', name: 'Small', price: 6, image: '/images/p-2-small.jpg' },
      { id: 'v2', name: 'Medium', price: 10, image: '/images/p-2-medium.jpg' },
      { id: 'v3', name: 'Large', price: 14, image: '/images/p-2-large.jpg' },
      { id: 'v4', name: 'Extra Large', price: 18, image: '/images/p-2-xl.jpg' },
      { id: 'v5', name: 'Family Size', price: 22, image: '/images/p-2-family.jpg' },
    ],
  },
  // Add more food items as needed
};

const FoodDetail = () => {
  const { id } = useParams();
  const foodItem = foodData[id];
  const [selectedVariant, setSelectedVariant] = useState(foodItem?.variants[0]);
  const { addToCart } = useCart();

  if (!foodItem) {
    return <div>Food item not found.</div>;
  }

  const handleAddToCart = () => {
    addToCart({
      id: foodItem.id + '-' + selectedVariant.id,
      name: foodItem.name + ' - ' + selectedVariant.name,
      price: selectedVariant.price,
      image: selectedVariant.image,
      quantity: 1,
    });
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <h1>{foodItem.name}</h1>
      <img
        src={selectedVariant.image}
        alt={`${foodItem.name} - ${selectedVariant.name}`}
        style={{ width: '100%', borderRadius: '8px' }}
      />
      <h2>Variants</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {foodItem.variants.map((variant) => (
          <li key={variant.id} style={{ marginBottom: '10px' }}>
            <label>
              <input
                type="radio"
                name="variant"
                value={variant.id}
                checked={selectedVariant.id === variant.id}
                onChange={() => setSelectedVariant(variant)}
              />
              {variant.name} - ${variant.price.toFixed(2)}
            </label>
          </li>
        ))}
      </ul>
      <button
        onClick={handleAddToCart}
        style={{
          backgroundColor: '#d32f2f',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '6px',
          cursor: 'pointer',
        }}
      >
        Add {selectedVariant.name} to Cart - ${selectedVariant.price.toFixed(2)}
      </button>
    </div>
  );
};

export default FoodDetail;
