import React, { useState } from 'react';

const HelpSupport = () => {
  const [activeFAQ, setActiveFAQ] = useState(null);

  const toggleFAQ = (index) => {
    setActiveFAQ(activeFAQ === index ? null : index);
  };

  return (
    <div className="help-support">
      <h1>Help & Support</h1>
      <p>
        If you have any questions or need assistance, please contact our support team.
      </p>
      <ul className="contact-list">
        <li>
          <span role="img" aria-label="email">ðŸ“§</span>
          <strong>Email:</strong> support@tastybites.com
        </li>
        <li>
          <span role="img" aria-label="phone">ðŸ“ž</span>
          <strong>Phone:</strong> +1 234 567 890
        </li>
        <li>
          <span role="img" aria-label="chat">ðŸ’¬</span>
          <strong>Live Chat:</strong> Available 9am - 9pm
        </li>
      </ul>
      <h2>FAQs</h2>
      <div>
        {[{
          question: 'How do I place an order?',
          answer: 'Browse the menu, add items to your cart, and proceed to checkout.'
        }, {
          question: 'What payment methods are accepted?',
          answer: 'We accept credit cards, debit cards, and popular digital wallets.'
        }, {
          question: 'How can I track my order?',
          answer: 'After placing an order, you can track its status in your account page.'
        }].map((faq, index) => (
          <div key={index}>
            <h3
              onClick={() => toggleFAQ(index)}
              style={{ cursor: 'pointer', userSelect: 'none', color: '#d32f2f', marginBottom: '8px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}
            >
              {faq.question}
              <span>{activeFAQ === index ? 'âˆ’' : '+'}</span>
            </h3>
            {activeFAQ === index && (
              <p style={{ fontSize: '16px', color: '#555', marginLeft: '10px', marginBottom: '20px' }}>{faq.answer}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default HelpSupport;
