import React from 'react';

const foodItems = [
  { id: 1, category: 'Pizza', name: 'Margherita', price: 8, image: '/images/p-1.jpg' },
  { id: 2, category: 'Pizza', name: 'Pepperoni', price: 10, image: '/images/p-2.jpg' },
  { id: 3, category: 'Burgers', name: 'Classic Burger', price: 7, image: '/images/g-1.jpg' },
  { id: 4, category: 'Sweets', name: 'Chocolate Cake', price: 5, image: '/images/g-5.jpg' },
  { id: 5, category: 'Drinks', name: 'Cold Drink', price: 2, image: '/images/p-5.jpg' },
  { id: 6, category: 'Ice Cream', name: 'Vanilla Ice Cream', price: 4, image: '/images/p-6.jpg' },
];

const RestaurantList = ({ selectedCategory, onAddToCart }) => {
  const filteredItems = selectedCategory
    ? foodItems.filter(item => item.category === selectedCategory)
    : foodItems;

  return (
    <div className="restaurant-list">
      {filteredItems.map(item => (
        <div key={item.id} className="food-item-card">
          <img src={item.image} alt={item.name} />
          <h4>{item.name}</h4>
          <p>${item.price.toFixed(2)}</p>
          <button onClick={() => onAddToCart(item)}>Add to Cart</button>
        </div>
      ))}
    </div>
  );
};

export default RestaurantList;
