import React from 'react';

const restaurants = [
  {
    id: 1,
    name: 'Pizza Palace',
    image: '/images/p-1.jpg',
    rating: 4.5,
    deliveryTime: '30 mins',
    costForTwo: '$20 for two',
    category: 'Pizza',
  },
  {
    id: 2,
    name: 'Burger Hub',
    image: '/images/g-1.jpg',
    rating: 4.2,
    deliveryTime: '25 mins',
    costForTwo: '$15 for two',
    category: 'Burgers',
  },
  {
    id: 3,
    name: 'Sweet Treats',
    image: '/images/g-5.jpg',
    rating: 4.7,
    deliveryTime: '20 mins',
    costForTwo: '$10 for two',
    category: 'Sweets',
  },
  {
    id: 4,
    name: 'Cool Drinks',
    image: '/images/p-5.jpg',
    rating: 4.0,
    deliveryTime: '15 mins',
    costForTwo: '$8 for two',
    category: 'Drinks',
  },
  {
    id: 5,
    name: 'Ice Cream Corner',
    image: '/images/p-6.jpg',
    rating: 4.6,
    deliveryTime: '18 mins',
    costForTwo: '$12 for two',
    category: 'Ice Cream',
  },
];

const RestaurantList = ({ selectedCategory }) => {
  const filteredRestaurants = selectedCategory
    ? restaurants.filter((r) => r.category === selectedCategory)
    : restaurants;

  return (
    <div className="restaurant-list" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill,minmax(250px,1fr))', gap: '20px' }}>
      {filteredRestaurants.map((restaurant) => (
        <div key={restaurant.id} className="restaurant-card" style={{ border: '1px solid #eee', borderRadius: '8px', overflow: 'hidden', cursor: 'pointer', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
          <img src={restaurant.image} alt={restaurant.name} style={{ width: '100%', height: '160px', objectFit: 'cover' }} />
          <div style={{ padding: '15px' }}>
            <h3 style={{ margin: '0 0 8px 0', color: '#d32f2f' }}>{restaurant.name}</h3>
            <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '14px', color: '#555' }}>
              <span>‚≠ê {restaurant.rating}</span>
              <span>{restaurant.deliveryTime}</span>
              <span>{restaurant.costForTwo}</span>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default RestaurantList;
