import React from 'react';
import { Link } from 'react-router-dom';

const foodItems = [
  {
    id: 1,
    name: 'Margherita Pizza',
    image: 'https://upload.wikimedia.org/wikipedia/commons/4/4b/Margherita_Originale.jpg',
    rating: 4.5,
    price: 8,
    variants: ['Small', 'Medium', 'Large'],
  },
  {
    id: 2,
    name: 'Pepperoni Pizza',
    image: 'https://upload.wikimedia.org/wikipedia/commons/d/d3/Supreme_pizza.jpg',
    rating: 4.7,
    price: 10,
    variants: ['Small', 'Medium', 'Large'],
  },
  {
    id: 3,
    name: 'Classic Burger',
    image: 'https://upload.wikimedia.org/wikipedia/commons/0/0b/RedDot_Burger.jpg',
    rating: 4.2,
    price: 7,
    variants: ['Single', 'Double'],
  },
  {
    id: 4,
    name: 'Chocolate Cake',
    image: 'https://upload.wikimedia.org/wikipedia/commons/7/70/Chocolate_cake_with_chocolate_frosting.jpg',
    rating: 4.8,
    price: 5,
    variants: ['Slice', 'Whole'],
  },
  {
    id: 5,
    name: 'Cold Drink',
    image: 'https://upload.wikimedia.org/wikipedia/commons/3/3e/Glass_of_coke.jpg',
    rating: 4.0,
    price: 2,
    variants: ['Regular', 'Large'],
  },
  {
    id: 6,
    name: 'Vanilla Ice Cream',
    image: 'https://upload.wikimedia.org/wikipedia/commons/7/7e/Vanilla_ice_cream_cone_2.jpg',
    rating: 4.6,
    price: 4,
    variants: ['Cup', 'Cone'],
  },
];

const RestaurantList = ({ selectedCategory, searchTerm }) => {
  const filteredItems = selectedCategory
    ? foodItems.filter((item) => item.name.toLowerCase().includes(selectedCategory.toLowerCase()))
    : foodItems;

  const searchedItems = searchTerm
    ? filteredItems.filter((item) => item.name.toLowerCase().includes(searchTerm.toLowerCase()))
    : filteredItems;

  return (
    <div className="food-list" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill,minmax(250px,1fr))', gap: '20px' }}>
      {searchedItems.map((item) => (
        <Link to={`/food/${item.id}`} key={item.id} style={{ textDecoration: 'none', color: 'inherit' }}>
          <div className="food-item-card" style={{ border: '1px solid #eee', borderRadius: '8px', overflow: 'hidden', cursor: 'pointer', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
            <img src={item.image} alt={item.name} style={{ width: '100%', height: '160px', objectFit: 'cover' }} />
            <div style={{ padding: '15px' }}>
              <h3 style={{ margin: '0 0 8px 0', color: '#d32f2f' }}>{item.name}</h3>
              <div style={{ fontSize: '14px', color: '#555' }}>
                <span>‚≠ê {item.rating}</span>
                <span style={{ marginLeft: '10px' }}>${item.price.toFixed(2)}</span>
              </div>
            </div>
          </div>
        </Link>
      ))}
    </div>
  );
};

export default RestaurantList;
