const RestaurantList = ({ selectedCategory, searchTerm }) => {
  const { addToCart } = useCart();
  const [message, setMessage] = useState('');

  const filteredItems = selectedCategory
    ? foodItems.filter((item) => item.category.toLowerCase() === selectedCategory.toLowerCase())
    : foodItems;

  const searchedItems = searchTerm
    ? filteredItems.filter((item) => item.name.toLowerCase().includes(searchTerm.toLowerCase()))
    : filteredItems;

  const handleAddToCart = (item) => {
    addToCart({ ...item, quantity: 1 });
    setMessage(item.name + ' added to cart!');
    setTimeout(() => setMessage(''), 3000);
  };

  return (
    <>
      {message && (
        <div style={{ position: 'fixed', top: '10px', right: '10px', backgroundColor: '#d32f2f', color: 'white', padding: '10px 20px', borderRadius: '8px', zIndex: 1000 }}>
          {message}
        </div>
      )}
      <div className="food-list" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(180px, 1fr))', gap: '20px' }}>
        {searchedItems.map((item) => (
          <div key={item.id} className="food-item-card" style={{ border: '1px solid #ddd', borderRadius: '8px', padding: '10px', backgroundColor: 'white' }}>
            <Link to={`/food/${item.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
              <img src={item.image} alt={item.name} style={{ width: '100%', borderRadius: '8px' }} />
              <h3>{item.name}</h3>
              <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '14px', color: '#555' }}>
                <span>⭐ {item.rating}</span>
                <span>${item.price.toFixed(2)}</span>
              </div>
            </Link>
            <button
              onClick={() => handleAddToCart(item)}
              style={{
                marginTop: '10px',
                backgroundColor: '#d32f2f',
                color: 'white',
                padding: '8px 12px',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                width: '100%',
              }}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </>
  );
};

export default RestaurantList;
