import React, { useState } from 'react';
import { auth } from '../firebaseConfig';
import { useNavigate, Link } from 'react-router-dom';
import FoodCategories from './FoodCategories';
import RestaurantList from './RestaurantList';
import CartSidebar from './CartSidebar';
import { useCart } from '../context/CartContext';

const Home = () => {
  const navigate = useNavigate();
  const [selectedCategory, setSelectedCategory] = useState(null);
  const { cartItems, addToCart, removeFromCart, updateQuantity } = useCart();

  const handleLogout = () => {
    auth.signOut().then(() => {
      navigate('/login');
    });
  };

  const handleSelectCategory = (category) => {
    setSelectedCategory(category);
  };

  return (
    <>
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px 20px', backgroundColor: '#d32f2f', color: 'white' }}>
        <Link to="/" className="logo" style={{ color: 'white', textDecoration: 'none', fontSize: '24px' }}>
          <i className="fas fa-utensils"></i> Food
        </Link>
        <nav className="navbar" style={{ display: 'flex', gap: '15px', alignItems: 'center' }}>
          <Link to="/">Home</Link>
          <Link to="/account">Account</Link>
          <Link to="/cart" style={{ position: 'relative' }}>
            Cart
            {cartItems.length > 0 && (
              <span style={{
                position: 'absolute',
                top: '-5px',
                right: '-10px',
                backgroundColor: 'yellow',
                color: 'black',
                borderRadius: '50%',
                padding: '2px 6px',
                fontSize: '12px',
                fontWeight: 'bold',
              }}>
                {cartItems.reduce((total, item) => total + item.quantity, 0)}
              </span>
            )}
          </Link>
          <button onClick={handleLogout} className="btn-logout" style={{ backgroundColor: 'transparent', border: 'none', color: 'white', cursor: 'pointer' }}>
            Logout
          </button>
        </nav>
      </header>

      <main className="main-content" style={{ padding: '20px' }}>
        <FoodCategories onSelectCategory={handleSelectCategory} />
        <RestaurantList selectedCategory={selectedCategory} onAddToCart={addToCart} />
        <CartSidebar
          cartItems={cartItems}
          onRemoveFromCart={removeFromCart}
          onUpdateQuantity={updateQuantity}
        />
      </main>
    </>
  );
};

export default Home;
