const Home = () => {
  const navigate = useNavigate();
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [location, setLocation] = useState('New York');
  const { cartItems, addToCart } = useCart();
  const [message, setMessage] = useState('');

  const handleLogout = () => {
    auth.signOut().then(() => {
      navigate('/login');
    });
  };

  const handleSelectCategory = (category) => {
    setSelectedCategory(category);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  const handleAddToCart = (item) => {
    addToCart(item);
    setMessage(`${item.name} added to cart`);
    setTimeout(() => setMessage(''), 2000);
  };

  useEffect(() => {
    const handleResize = () => {
      // Add logic to handle window resize
    };
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const formatPriceINR = (price) => {
    return price.toLocaleString('en-IN', { style: 'currency', currency: 'INR' });
  };

  return (
    <>
      <header className="header">
        <Link to="/" className="logo">
          Tasty Bites
        </Link>
        <nav className="navbar">
          <Link to="/">Home</Link>
          <Link to="/account">Account</Link>
          <Link to="/cart" className="cart-link" style={{ position: 'relative' }}>
            Cart
            {cartItems.length > 0 && (
              <span className="cart-badge">
                {cartItems.reduce((total, item) => total + item.quantity, 0)}
              </span>
            )}
          </Link>
          <button onClick={handleLogout} className="btn-logout">
            Logout
          </button>
        </nav>
      </header>

      <section
        className="hero-section"
        style={{ backgroundImage: `url(${process.env.PUBLIC_URL + '/images/home-bg.jpg'})` }}
      >
        <h1>Delicious Food Delivered To You</h1>
        <p>Explore the best restaurants and dishes in your city</p>
      </section>

      {message && <div className="add-cart-message">{message}</div>}

      <main className="main-content">
        <section className="food-suggestions">
          <h2>Food Suggestions</h2>
          <div className="food-gallery">
            {foodItems.map((item) => (
              <div key={item.id} className="food-item">
                <img src={item.image} alt={item.name} />
                <div className="food-name">{item.name}</div>
                <div className="food-price">{formatPriceINR(item.price)}</div>
                <button onClick={() => handleAddToCart(item)} className="btn-add-cart">
                  Add to Cart
                </button>
              </div>
            ))}
          </div>
        </section>

        <section className="popular-food">
          <h2>Popular Food</h2>
          <FoodCategories onSelectCategory={handleSelectCategory} />
          <RestaurantList selectedCategory={selectedCategory} searchTerm={searchTerm} />
        </section>

        <section className="help-support-section">
          <HelpSupport />
        </section>

        <section className="review-section">
          <Review />
        </section>
      </main>

      <footer className="footer">
        &copy; {new Date().getFullYear()} Tasty Bites. All rights reserved.
      </footer>
    </>
  );
};

export default Home;
