import React from 'react';
import { useParams } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const restaurantMenus = {
  1: [
    { id: 101, name: 'Butter Chicken', description: 'Creamy spiced chicken curry', price: 250 },
    { id: 102, name: 'Naan', description: 'Soft Indian bread', price: 40 },
    { id: 103, name: 'Paneer Tikka', description: 'Grilled cottage cheese cubes', price: 200 },
  ],
  2: [
    { id: 201, name: 'Classic Cheeseburger', description: 'Beef patty with cheese', price: 150 },
    { id: 202, name: 'Fries', description: 'Crispy golden fries', price: 80 },
    { id: 203, name: 'Cola', description: 'Chilled soft drink', price: 50 },
  ],
  3: [
    { id: 301, name: 'Chocolate Brownie', description: 'Rich chocolate dessert', price: 120 },
    { id: 302, name: 'Vanilla Ice Cream', description: 'Creamy vanilla flavor', price: 100 },
    { id: 303, name: 'Cupcake', description: 'Sweet baked treat', price: 90 },
  ],
};

const Menu = () => {
  const { id } = useParams();
  const menuItems = restaurantMenus[id] || [];
  const { addToCart } = useCart();

  return (
    <div style={{ maxWidth: '800px', margin: 'auto', padding: '20px' }}>
      <h2 style={{ color: '#d32f2f', marginBottom: '20px' }}>Menu</h2>
      {menuItems.length === 0 ? (
        <p>No menu items found for this restaurant.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {menuItems.map(item => (
            <li key={item.id} style={{
              borderBottom: '1px solid #ddd',
              padding: '10px 0',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
            }}>
              <div>
                <h4 style={{ margin: '0 0 5px' }}>{item.name}</h4>
                <p style={{ margin: 0, color: '#555' }}>{item.description}</p>
              </div>
              <div>
                <span style={{ marginRight: '15px', fontWeight: 'bold' }}>â‚¹{item.price}</span>
                <button onClick={() => addToCart(item)} style={{
                  backgroundColor: '#d32f2f',
                  color: 'white',
                  border: 'none',
                  padding: '5px 10px',
                  borderRadius: '5px',
                  cursor: 'pointer',
                }}>
                  Add to Cart
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Menu;
