import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const foodData = {
  1: {
    id: 1,
    name: 'Margherita Pizza',
    image: '/images/p-1.jpg',
    variants: [
      { id: 'v1', name: 'Small', price: 5 },
      { id: 'v2', name: 'Medium', price: 8 },
      { id: 'v3', name: 'Large', price: 12 },
    ],
  },
  2: {
    id: 2,
    name: 'Pepperoni Pizza',
    image: '/images/p-2.jpg',
    variants: [
      { id: 'v1', name: 'Small', price: 6 },
      { id: 'v2', name: 'Medium', price: 10 },
      { id: 'v3', name: 'Large', price: 14 },
    ],
  },
  // Add more food items as needed
};

const FoodDetail = () => {
  const { id } = useParams();
  const foodItem = foodData[id];
  const [selectedVariant, setSelectedVariant] = useState(foodItem?.variants[0]);

  if (!foodItem) {
    return <div>Food item not found.</div>;
  }

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <h1>{foodItem.name}</h1>
      <img src={foodItem.image} alt={foodItem.name} style={{ width: '100%', borderRadius: '8px' }} />
      <h2>Variants</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {foodItem.variants.map((variant) => (
          <li key={variant.id} style={{ marginBottom: '10px' }}>
            <label>
              <input
                type="radio"
                name="variant"
                value={variant.id}
                checked={selectedVariant.id === variant.id}
                onChange={() => setSelectedVariant(variant)}
              />
              {variant.name} - ${variant.price.toFixed(2)}
            </label>
          </li>
        ))}
      </ul>
      <button style={{ backgroundColor: '#d32f2f', color: 'white', padding: '10px 20px', border: 'none', borderRadius: '6px', cursor: 'pointer' }}>
        Add {selectedVariant.name} to Cart - ${selectedVariant.price.toFixed(2)}
      </button>
    </div>
  );
};

export default FoodDetail;
