import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const foodItems = [
  {
    id: 1,
    name: 'Margherita Pizza',
    image: '/images/p-1.jpg',
    rating: 4.5,
    price: 8,
    variants: ['Small', 'Medium', 'Large'],
  },
  {
    id: 2,
    name: 'Pepperoni Pizza',
    image: '/images/p-2.jpg',
    rating: 4.7,
    price: 10,
    variants: ['Small', 'Medium', 'Large'],
  },
  {
    id: 3,
    name: 'Classic Burger',
    image: '/images/g-1.jpg',
    rating: 4.2,
    price: 7,
    variants: ['Single', 'Double'],
  },
  {
    id: 4,
    name: 'Chocolate Cake',
    image: '/images/g-5.jpg',
    rating: 4.8,
    price: 5,
    variants: ['Slice', 'Whole'],
  },
  {
    id: 5,
    name: 'Cold Drink',
    image: '/images/p-5.jpg',
    rating: 4.0,
    price: 2,
    variants: ['Regular', 'Large'],
  },
  {
    id: 6,
    name: 'Vanilla Ice Cream',
    image: '/images/p-6.jpg',
    rating: 4.6,
    price: 4,
    variants: ['Cup', 'Cone'],
  },
];

const RestaurantList = ({ selectedCategory, searchTerm }) => {
  const { addToCart } = useCart();
  const [message, setMessage] = useState('');

  const filteredItems = selectedCategory
    ? foodItems.filter((item) => item.name.toLowerCase().includes(selectedCategory.toLowerCase()))
    : foodItems;

  const searchedItems = searchTerm
    ? filteredItems.filter((item) => item.name.toLowerCase().includes(searchTerm.toLowerCase()))
    : filteredItems;

  const handleAddToCart = (item) => {
    addToCart(item);
    setMessage(item.name + ' added to cart!');
    setTimeout(() => setMessage(''), 3000);
  };

  return (
    <>
      {message && (
        <div style={{ position: 'fixed', top: '10px', right: '10px', backgroundColor: '#d32f2f', color: 'white', padding: '10px 20px', borderRadius: '8px', zIndex: 1000 }}>
          {message}
        </div>
      )}
      <div className="food-list">
        {searchedItems.map((item) => (
          <div key={item.id} className="food-item-card">
            <Link to={`/food/${item.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
              <img src={item.image} alt={item.name} />
              <h3>{item.name}</h3>
              <div>
                <span>‚≠ê {item.rating}</span>
                <span>${item.price.toFixed(2)}</span>
              </div>
            </Link>
            <button onClick={() => handleAddToCart(item)} style={{ margin: '10px 15px 15px 15px', width: 'calc(100% - 30px)' }}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </>
  );
};

export default RestaurantList;
