import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

// Generate 50+ food items with categories and variants
const foodItems = Array.from({ length: 50 }, (_, i) => {
  const id = i + 1;
  const categories = ['Cold Drink', 'Ice Cream', 'Burger', 'Pizza', 'Sweet'];
  const category = categories[i % categories.length];
  const baseNames = {
    'Cold Drink': ['Coke', 'Pepsi', 'Sprite', 'Fanta', 'Mountain Dew'],
    'Ice Cream': ['Vanilla', 'Chocolate', 'Strawberry', 'Mint', 'Mango'],
    'Burger': ['Classic', 'Cheese', 'Bacon', 'Double Patty', 'Veggie'],
    'Pizza': ['Margherita', 'Pepperoni', 'BBQ Chicken', 'Veggie', 'Hawaiian'],
    'Sweet': ['Gulab Jamun', 'Rasgulla', 'Jalebi', 'Kheer', 'Barfi'],
  };
  const variants = baseNames[category];
  const variantIndex = i % variants.length;
  const name = `${category} - ${variants[variantIndex]}`;
  const imageMap = {
    'Cold Drink': '/images/cold-drink-coke.jpg',
    'Ice Cream': '/images/ice-cream-vanilla.jpg',
    'Burger': '/images/burger-classic.jpg',
    'Pizza': '/images/pizza-margherita.jpg',
    'Sweet': '/images/sweet-gulabjamun.jpg',
  };
  const image = imageMap[category];
  const priceBase = {
    'Cold Drink': 2,
    'Ice Cream': 3,
    'Burger': 5,
    'Pizza': 8,
    'Sweet': 4,
  };
  const price = priceBase[category] + (variantIndex * 0.5);

  return {
    id,
    name,
    category,
    image,
    rating: (Math.random() * 1.5 + 3.5).toFixed(1), // random rating between 3.5 and 5.0
    price,
  };
});

const RestaurantList = ({ selectedCategory, searchTerm }) => {
  const { addToCart } = useCart();
  const [message, setMessage] = useState('');

  const filteredItems = selectedCategory
    ? foodItems.filter((item) => item.category.toLowerCase() === selectedCategory.toLowerCase())
    : foodItems;

  const searchedItems = searchTerm
    ? filteredItems.filter((item) => item.name.toLowerCase().includes(searchTerm.toLowerCase()))
    : filteredItems;

  const handleAddToCart = (item) => {
    addToCart({ ...item, quantity: 1 });
    setMessage(item.name + ' added to cart!');
    setTimeout(() => setMessage(''), 3000);
  };

  return (
    <>
      {message && (
        <div style={{ position: 'fixed', top: '10px', right: '10px', backgroundColor: '#d32f2f', color: 'white', padding: '10px 20px', borderRadius: '8px', zIndex: 1000 }}>
          {message}
        </div>
      )}
      <div className="food-list" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(180px, 1fr))', gap: '20px' }}>
        {searchedItems.map((item) => (
          <div key={item.id} className="food-item-card" style={{ border: '1px solid #ddd', borderRadius: '8px', padding: '10px', backgroundColor: 'white' }}>
            <Link to={`/food/${item.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
              <img src={item.image} alt={item.name} style={{ width: '100%', borderRadius: '8px' }} />
              <h3>{item.name}</h3>
              <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '14px', color: '#555' }}>
                <span>‚≠ê {item.rating}</span>
                <span>${item.price.toFixed(2)}</span>
              </div>
            </Link>
            <button
              onClick={() => handleAddToCart(item)}
              style={{
                marginTop: '10px',
                backgroundColor: '#d32f2f',
                color: 'white',
                padding: '8px 12px',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                width: '100%',
              }}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </>
  );
};

export default RestaurantList;
