import React, { useEffect, useState } from 'react';
import { auth } from '../firebaseConfig';

const UserAccount = () => {
  const [user, setUser] = useState(null);
  const [recentOrders, setRecentOrders] = useState([]);

  useEffect(() => {
    const currentUser = auth.currentUser;
    if (currentUser) {
      setUser({
        name: currentUser.displayName || 'User',
        email: currentUser.email,
      });
      // TODO: Fetch recent orders from backend or Firebase
      // For now, using mock data
      setRecentOrders([
        { id: 1, item: 'Paneer Butter Masala', date: '2024-06-01', amount: 250 },
        { id: 2, item: 'Margherita Pizza', date: '2024-05-28', amount: 300 },
      ]);
    }
  }, []);

  return (
    <div className="user-account-container" style={{ padding: '20px', maxWidth: '600px', margin: 'auto' }}>
      <h2 style={{ color: '#d32f2f', marginBottom: '20px' }}>User Account</h2>
      {user ? (
        <>
          <div className="user-details" style={{ marginBottom: '30px' }}>
            <h3>User Details</h3>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
          </div>
          <div className="recent-orders">
            <h3>Recent Orders</h3>
            {recentOrders.length === 0 ? (
              <p>No recent orders found.</p>
            ) : (
              <ul>
                {recentOrders.map(order => (
                  <li key={order.id} style={{ marginBottom: '10px' }}>
                    <span>{order.item} - </span>
                    <span>{order.date} - </span>
                    <span>â‚¹{order.amount}</span>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </>
      ) : (
        <p>Please login to view your account details.</p>
      )}
    </div>
  );
};

export default UserAccount;
