import React, { useState, useEffect } from 'react';
import { auth } from '../firebaseConfig';
import { useNavigate } from 'react-router-dom';

const UserAccount = () => {
  const navigate = useNavigate();

  const [profile, setProfile] = useState(() => {
    const savedProfile = localStorage.getItem('userProfile');
    return savedProfile ? JSON.parse(savedProfile) : { name: '', email: '', phone: '' };
  });

  const [orders] = useState([
    { id: '1001', date: '2024-04-01', status: 'Delivered', total: 25.0 },
    { id: '1002', date: '2024-04-10', status: 'In Progress', total: 15.5 },
  ]);

  const [activeSection, setActiveSection] = useState('profile');

  useEffect(() => {
    const user = auth.currentUser;
    if (user && !profile.name && !profile.email) {
      setProfile({
        name: user.displayName || '',
        email: user.email || '',
        phone: '',
      });
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('userProfile', JSON.stringify(profile));
  }, [profile]);

  const handleLogout = () => {
    auth.signOut().then(() => {
      navigate('/login');
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile((prev) => ({ ...prev, [name]: value }));
  };

  const formatPriceINR = (price) => {
    return new Intl.NumberFormat('en-IN', { style: 'currency', currency: 'INR' }).format(price);
  };

  return (
    <div className="main-content" style={{ maxWidth: '1200px', margin: '0 auto', display: 'flex', gap: '30px', padding: '40px 20px' }}>
      {/* Sidebar */}
      <aside style={{ width: '250px', backgroundColor: '#fff', borderRadius: '10px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)', padding: '20px' }}>
        <div style={{ textAlign: 'center', marginBottom: '30px' }}>
          <div style={{
            width: '100px',
            height: '100px',
            borderRadius: '50%',
            backgroundColor: '#d32f2f',
            color: 'white',
            fontSize: '48px',
            lineHeight: '100px',
            margin: '0 auto',
            fontWeight: '700',
            userSelect: 'none'
          }}>
            {profile.name ? profile.name.charAt(0).toUpperCase() : 'U'}
          </div>
          <h3 style={{ marginTop: '15px', color: '#d32f2f' }}>{profile.name || 'User'}</h3>
          <p style={{ color: '#555', fontSize: '14px' }}>{profile.email || 'No email'}</p>
        </div>
        <nav>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            <li
              onClick={() => setActiveSection('profile')}
              style={{
                padding: '12px 20px',
                cursor: 'pointer',
                backgroundColor: activeSection === 'profile' ? '#fce4e4' : 'transparent',
                color: activeSection === 'profile' ? '#d32f2f' : '#333',
                fontWeight: activeSection === 'profile' ? '700' : '400',
                borderRadius: '6px',
                marginBottom: '10px',
                userSelect: 'none'
              }}
            >
              Profile Details
            </li>
            <li
              onClick={() => setActiveSection('orders')}
              style={{
                padding: '12px 20px',
                cursor: 'pointer',
                backgroundColor: activeSection === 'orders' ? '#fce4e4' : 'transparent',
                color: activeSection === 'orders' ? '#d32f2f' : '#333',
                fontWeight: activeSection === 'orders' ? '700' : '400',
                borderRadius: '6px',
                userSelect: 'none'
              }}
            >
              My Orders
            </li>
            <li>
              <button
                onClick={handleLogout}
                className="btn-logout"
                style={{ width: '100%', marginTop: '30px', backgroundColor: '#d32f2f', padding: '12px', fontWeight: '700', fontSize: '16px', borderRadius: '6px', color: 'white', border: 'none', cursor: 'pointer' }}
              >
                Logout
              </button>
            </li>
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <section style={{ flexGrow: 1 }}>
        {activeSection === 'profile' && (
          <div style={{ backgroundColor: '#fff', padding: '30px 40px', borderRadius: '10px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }}>
            <h2 style={{ color: '#d32f2f', fontWeight: '700', marginBottom: '25px' }}>Profile Details</h2>
            <form>
              <label htmlFor="name" style={{ display: 'block', marginBottom: '10px', fontWeight: '600', color: '#333' }}>Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={profile.name}
                onChange={handleChange}
                style={{ width: '100%', padding: '12px', marginBottom: '20px', border: '1px solid #ccc', borderRadius: '6px', fontSize: '16px', boxSizing: 'border-box' }}
              />

              <label htmlFor="email" style={{ display: 'block', marginBottom: '10px', fontWeight: '600', color: '#333' }}>Email</label>
              <input
                type="text"
                id="email"
                name="email"
                value={profile.email}
                onChange={handleChange}
                style={{ width: '100%', padding: '12px', marginBottom: '20px', border: '1px solid #ccc', borderRadius: '6px', fontSize: '16px', boxSizing: 'border-box' }}
              />

              <label htmlFor="phone" style={{ display: 'block', marginBottom: '10px', fontWeight: '600', color: '#333' }}>Phone</label>
              <input
                type="text"
                id="phone"
                name="phone"
                value={profile.phone}
                onChange={handleChange}
                style={{ width: '100%', padding: '12px', marginBottom: '20px', border: '1px solid #ccc', borderRadius: '6px', fontSize: '16px', boxSizing: 'border-box' }}
              />
            </form>
          </div>
        )}

        {activeSection === 'orders' && (
          <div style={{ backgroundColor: '#fff', padding: '30px 40px', borderRadius: '10px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }}>
            <h2 style={{ color: '#d32f2f', fontWeight: '700', marginBottom: '25px' }}>My Orders</h2>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr>
                  <th style={{ padding: '16px', borderBottom: '1px solid #eee', fontSize: '16px', textAlign: 'left' }}>Order ID</th>
                  <th style={{ padding: '16px', borderBottom: '1px solid #eee', fontSize: '16px', textAlign: 'left' }}>Date</th>
                  <th style={{ padding: '16px', borderBottom: '1px solid #eee', fontSize: '16px', textAlign: 'left' }}>Status</th>
                  <th style={{ padding: '16px', borderBottom: '1px solid #eee', fontSize: '16px', textAlign: 'left' }}>Total</th>
                </tr>
              </thead>
              <tbody>
                {orders.map((order) => (
                  <tr key={order.id} style={{ cursor: 'pointer', transition: 'background-color 0.3s ease' }} onMouseEnter={e => e.currentTarget.style.backgroundColor = '#f9f9f9'} onMouseLeave={e => e.currentTarget.style.backgroundColor = 'transparent'}>
                    <td style={{ padding: '16px', borderBottom: '1px solid #eee' }}>{order.id}</td>
                    <td style={{ padding: '16px', borderBottom: '1px solid #eee' }}>{order.date}</td>
                    <td style={{ padding: '16px', borderBottom: '1px solid #eee' }}>{order.status}</td>
                    <td style={{ padding: '16px', borderBottom: '1px solid #eee' }}>{formatPriceINR(order.total)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </section>
    </div>
  );
};

export default UserAccount;
