import React from 'react';

const reviews = [
  {
    id: 1,
    name: 'Priya Patel',
    rating: 4,
    comment: 'The Tasty Sweets Are Simply Divine. Every Bite Of The Cake Was Moist And Delicious. A Real Treat!',
    image: 'https://randomuser.me/api/portraits/women/65.jpg',
  },
  {
    id: 2,
    name: 'Rahul Gupta',
    rating: 4,
    comment: 'TastyBites Breakfast Is Truly Satisfying! They Always Hit The Spot For Me.',
    image: 'https://randomuser.me/api/portraits/men/43.jpg',
  },
  {
    id: 3,
    name: 'Meera Pandi',
    rating: 4,
    comment: "It's The Ultimate Comfort Food To Start Your Day With. Highly Recommend!",
    image: 'https://randomuser.me/api/portraits/women/44.jpg',
  },
];

const Review = () => {
  const renderStars = (count) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} style={{ color: i <= count ? '#d32f2f' : '#ccc', fontSize: '18px' }}>
          &#9733;
        </span>
      );
    }
    return stars;
  };

  return (
    <div className="review-section">
      <h2 className="review-heading">
        Our Customers <span className="highlight">Reviews</span>
      </h2>
      <div className="review-cards">
        {reviews.map((review) => (
          <div key={review.id} className="review-card">
            <div className="avatar-wrapper">
              <img src={review.image} alt={review.name} className="avatar" />
            </div>
            <h3 className="reviewer-name">{review.name}</h3>
            <div className="star-rating">{renderStars(review.rating)}</div>
            <p className="review-text">{review.comment}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Review;
