import React from 'react';
import { useCart } from '../context/CartContext';

const CartPage = () => {
  const { cartItems, updateQuantity, removeFromCart } = useCart();

  const totalPrice = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <h1>Your Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ borderBottom: '1px solid #ddd' }}>
              <th style={{ textAlign: 'left', padding: '8px' }}>Item</th>
              <th style={{ textAlign: 'center', padding: '8px' }}>Quantity</th>
              <th style={{ textAlign: 'right', padding: '8px' }}>Price</th>
              <th style={{ textAlign: 'right', padding: '8px' }}>Total</th>
              <th style={{ padding: '8px' }}>Remove</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((item) => (
              <tr key={item.id} style={{ borderBottom: '1px solid #eee' }}>
                <td style={{ padding: '8px' }}>{item.name}</td>
                <td style={{ padding: '8px', textAlign: 'center' }}>
                  <input
                    type="number"
                    min="1"
                    value={item.quantity}
                    onChange={(e) => updateQuantity(item.id, parseInt(e.target.value))}
                    style={{ width: '50px' }}
                  />
                </td>
                <td style={{ padding: '8px', textAlign: 'right' }}>${item.price.toFixed(2)}</td>
                <td style={{ padding: '8px', textAlign: 'right' }}>${(item.price * item.quantity).toFixed(2)}</td>
                <td style={{ padding: '8px', textAlign: 'center' }}>
                  <button onClick={() => removeFromCart(item.id)} style={{ backgroundColor: '#d32f2f', color: 'white', border: 'none', padding: '5px 10px', cursor: 'pointer' }}>
                    Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td colSpan="3" style={{ textAlign: 'right', padding: '8px', fontWeight: 'bold' }}>Total:</td>
              <td style={{ textAlign: 'right', padding: '8px', fontWeight: 'bold' }}>${totalPrice.toFixed(2)}</td>
              <td></td>
            </tr>
          </tfoot>
        </table>
      )}
    </div>
  );
};

export default CartPage;
