import React from 'react';

const categories = [
  { id: 1, name: 'Pizza', image: '/images/p-1.jpg' },
  { id: 2, name: 'Burgers', image: '/images/g-1.jpg' },
  { id: 3, name: 'Sweets', image: '/images/g-5.jpg' },
  { id: 4, name: 'Drinks', image: '/images/p-5.jpg' },
  { id: 5, name: 'Ice Cream', image: '/images/p-6.jpg' },
];

const FoodCategories = ({ onSelectCategory }) => {
  return (
    <div className="food-categories">
      {categories.map(category => (
        <div key={category.id} className="category-card" onClick={() => onSelectCategory(category.name)}>
          <img src={category.image} alt={category.name} />
          <h4>{category.name}</h4>
        </div>
      ))}
    </div>
  );
};

export default FoodCategories;
