import React, { useState } from 'react';

const Review = () => {
  const [reviews, setReviews] = useState([
    { id: 1, name: 'John Doe', rating: 5, comment: 'Great food and quick delivery!', image: 'https://randomuser.me/api/portraits/men/1.jpg' },
    { id: 2, name: 'Jane Smith', rating: 4, comment: 'Tasty bites, will order again.', image: 'https://randomuser.me/api/portraits/women/2.jpg' },
  ]);
  const [newReview, setNewReview] = useState({ name: '', rating: 0, comment: '', image: '' });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewReview({ ...newReview, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newReview.name && newReview.rating > 0 && newReview.comment) {
      setReviews([...reviews, { ...newReview, id: reviews.length + 1 }]);
      setNewReview({ name: '', rating: 0, comment: '', image: '' });
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h1>Customer Reviews</h1>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {reviews.map((review) => (
          <li key={review.id} style={{ borderBottom: '1px solid #ccc', padding: '10px 0', display: 'flex', alignItems: 'center' }}>
            <img src={review.image} alt={review.name} style={{ width: '100px', height: '100px', borderRadius: '50%', marginRight: '15px' }} />
            <div>
              <strong>{review.name}</strong> - Rating: {review.rating}/5
              <p>{review.comment}</p>
            </div>
          </li>
        ))}
      </ul>
      <h2>Add a Review</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label><br />
          <input type="text" name="name" value={newReview.name} onChange={handleInputChange} required />
        </div>
        <div>
          <label>Rating:</label><br />
          <select name="rating" value={newReview.rating} onChange={handleInputChange} required>
            <option value={0}>Select rating</option>
            <option value={1}>1 - Poor</option>
            <option value={2}>2 - Fair</option>
            <option value={3}>3 - Good</option>
            <option value={4}>4 - Very Good</option>
            <option value={5}>5 - Excellent</option>
          </select>
        </div>
        <div>
          <label>Comment:</label><br />
          <textarea name="comment" value={newReview.comment} onChange={handleInputChange} required />
        </div>
        <button type="submit" style={{ marginTop: '10px' }}>Submit Review</button>
      </form>
    </div>
  );
};

export default Review;
