import React, { useState, useEffect } from 'react';
import { auth } from '../firebaseConfig';
import { useNavigate } from 'react-router-dom';

const UserAccount = () => {
  const navigate = useNavigate();

  const [profile, setProfile] = useState({
    name: '',
    email: '',
    phone: '',
  });

  const [orders] = useState([
    { id: '1001', date: '2024-04-01', status: 'Delivered', total: '$25.00' },
    { id: '1002', date: '2024-04-10', status: 'In Progress', total: '$15.50' },
  ]);

  useEffect(() => {
    const user = auth.currentUser;
    if (user) {
      setProfile({
        name: user.displayName || '',
        email: user.email || '',
        phone: '',
      });
    }
  }, []);

  const handleLogout = () => {
    auth.signOut().then(() => {
      navigate('/login');
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div className="main-content">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1>My Account</h1>
        <button onClick={handleLogout} className="btn-logout" style={{ marginLeft: 'auto' }}>
          Logout
        </button>
      </div>

      <section className="profile-section">
        <h2>Profile Details</h2>
        <form>
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={profile.name}
            onChange={handleChange}
          />

          <label htmlFor="email">Email</label>
          <input
            type="text"
            id="email"
            name="email"
            value={profile.email}
            onChange={handleChange}
          />

          <label htmlFor="phone">Phone</label>
          <input
            type="text"
            id="phone"
            name="phone"
            value={profile.phone}
            onChange={handleChange}
          />
        </form>
      </section>

      <section className="orders-section">
        <h2>My Orders</h2>
        <table>
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Date</th>
              <th>Status</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order.id}>
                <td>{order.id}</td>
                <td>{order.date}</td>
                <td>{order.status}</td>
                <td>{order.total}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
};

export default UserAccount;
